import * as data from "./tasks.json"


export function getTasks() {
    if (localStorage.getItem('tasks')!=null) {
        data.tasks = JSON.parse(localStorage.getItem('tasks'))
    }
        return data.tasks
    
    
}
export function getEngineers() {
    if (localStorage.getItem('engineers')!=null) {
        data.engineers = JSON.parse(localStorage.getItem('engineers'))
    }
    return data.engineers
}

export function addTask(task, personID) {
    var temp = 
    {
        "id": newTaskId(getTasks()),
        "text": task,
        "personID": personID,
        "done": false,
    }
    data.tasks.push(temp);
    save()
}

export function save() {
    var tasks = data.tasks
    var engineers = data.engineers
    localStorage.setItem('tasks', JSON.stringify(tasks));
    localStorage.setItem('engineers', JSON.stringify(engineers));

}

export function addEngineer(name, role) {
    var temp = 
    {
        "id": newTaskId(getEngineers()),
        "name": name,
        "role": role,
    }
    data.engineers.push(temp);
}

export function getEngineersName(id) {
    for(let i=0; i<getEngineers().length; i++) {
        if(getEngineers()[i].id == id) {
            return getEngineers()[i].name
        }
    }
}








function newTaskId(arr) {
    var idCount = 0 
    arr.map((value,index) => {
        if(value.id > idCount) {
            idCount = value.id+1;
        }
    })
    return idCount;
}
