import * as data from "./tasks.json"


export function getTasks() {
    return data.tasks;
}
export function getEngineers() {
    return data.engineers
}

export function addTask(task, engineer) {
    var idCount = 0 
    getTasks().map((value,index) => {
        if(value.id > idCount) {
            idCount = value.id+1;
        }
    })
    

}

function newTaskId(arr) {
    var idCount = 0 
    arr.map((value,index) => {
        if(value.id > idCount) {
            idCount = value.id+1;
        }
    })
    return idCount;
}
