import React, { Component } from 'react';
import Game from './components/Game'
import './App.css';
import * as data from './store/tasks.json'
import * as action from './store/actions.js'
import { act } from 'react-dom/test-utils';
import Task from './components/Task'

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            tasks: []
        };
      }

    componentWillMount() {
    
//         localStorage.setItem('myData', data);
 
// // getter
// localStorage.getItem('myData');
 
// // remove
// localStorage.removeItem('myData');
 
// // remove all
// localStorage.clear();

        if(localStorage.getItem('data')) {
            var data = JSON.parse(localStorage.getItem('data'))
            this.setState({tasks: data})
        }else {
            this.setState({tasks: action.getTasks()})
            localStorage.setItem('data', JSON.stringify(data.tasks));
        }
    }
    handleAdd() {
        action.addTask(document.getElementById('newtask').value, document.getElementById('newtask_e').value)
        this.setState({tasks: action.getTasks()})
    }
  
    render() {
        return (
            <div className='body'>
                <label>View: </label>
                <select id='view'>
                    <option value='--'>--</option>
                    <option value='Admin'>Admin</option>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                </select>


            <div>
                <label>Task:</label>
                <br/>
                <textarea id='newtask'/>
                <br/>
                <label>Assigned:</label>
                <select id='newtask_e'>
                {action.getEngineers().map((value,index) => {
                    return (<option value={value.id}>{value.name}</option>)
                })}
                </select>
                <br/>
                <button onClick={() => this.handleAdd()}>Add task</button>
            </div>


            {this.state.tasks.map((value, index) => {
                return (
                    <Task key={index} task={value}/>
                )
            })}
            
            </div>
        );
    }
}

export default App;
