import React, { Component } from 'react';
import * as action from '../store/actions.js'
import App from '../App'

class Task extends Component {

deleteTask(task){
    action.deleteTask(task)
    location.reload();

}
    render() {
        var task = this.props.task


        return (
            <div className='task-container'>
                <button className='remove' onClick={() => this.deleteTask(task.id)}>&#10006;</button>
                <label>Task:</label>
                <p>{task.text}</p>
                <label>Assigned:</label>
                <b>{action.getEngineersName(task.personID)}</b>
            </div>
        );
    }
}

export default Task;
