import React, { Component } from 'react';
import Game from './components/Game'
import './App.css';
import * as data from './store/tasks.json'
import * as action from './store/actions.js'
import { act } from 'react-dom/test-utils';
import Task from './components/Task'

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            tasks: []
        };
      }
    componentWillMount() {
        this.setState({tasks: action.getTasks()})
    }
    handleAdd() {
        action.addTask(
            document.getElementById('newtask').value,
            document.getElementById('newtask_e').value
            )
        this.setState({tasks: action.getTasks()})
    }
    handleView() {

    }
  
    render() {
        return (
            <div className='body'>
                <label>View: </label>
                <select id='view' onChange={() => this.handleView()}>
                    <option value={-1}>--</option>
                    <option value={0}>Admin</option>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                </select>


            <div className='admin-panel'>
                <div style={{"display":"block"}}>
                    <label>Task:</label>
                    {/* <br/> */}
                    <textarea id='newtask'/>
                    {/* <br/> */}
                    <label>Assigned:</label>
                    <select id='newtask_e'>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                    </select>
                    <br/>
                    <button onClick={() => this.handleAdd()}>Add task</button>
                </div>
                <div>
                    <label>Task:</label>
                    <br/>
                    <textarea id='newtask'/>
                    <br/>
                    <label>Assigned:</label>
                    <select id='newtask_e'>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                    </select>
                    <br/>
                    <button onClick={() => this.handleAdd()}>Add task</button>
                </div>
            </div>


            {this.state.tasks.map((value, index) => {
                return (
                    <Task key={index} task={value}/>
                )
            })}
            
            </div>
        );
    }
}

export default App;
