import React, { Component } from 'react';
import Game from './components/Game'
import './App.css';
// import * as data from './store/tasks.json'
import * as action from './store/actions.js'
import { act } from 'react-dom/test-utils';
import Task from './components/Task'

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            tasks: []
        };
      }

    resetValues() {
        document.getElementsByTagName('input').value = ''
    }

    addTask() {
        action.addTask(document.getElementById('newtask').value, document.getElementById('newtask_e').value)
        this.setState({tasks: action.getTasks()})
        this.resetValues()
    }

    addEngineer() {
        action.addEngineer( document.getElementById('e_name').value, document.getElementById('e_role').value)
        this.setState({tasks: action.getTasks()})
    }
    handleView() {
        var user = document.getElementById('view').value;
        if( user == 0) {
            document.getElementById('admin-panel').classList.remove('hide')
            this.setState({tasks: action.getTasks()})
        }else {
            document.getElementById('admin-panel').className = 'hide'
            document.getElementById('remove').classList.add('hide');
            this.setState({ tasks: action.getTaskforEngineer(user) });
        }

    }
  
    render() {
        return (
            <div className='body'>
                <label>Pick an user: </label>
                <select id='view' onChange={() => this.handleView()}>
                    <option value={-1}>--</option>
                    <option value={0}>Admin</option>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                </select>


            <div id='admin-panel' className='hide'>
                <div style={{"display":"block"}}>
                    <label>Task:</label><br/>
                    <textarea id='newtask'/><br/>
                    <label>Engineer:</label>
                    <select id='newtask_e'>
                    {action.getEngineers().map((value,index) => {
                        return (<option value={value.id}>{value.name}</option>)
                    })}
                    </select>
                    <br/>
                    <button onClick={() => this.addTask()}>Add task</button>
                </div>
                <div className='center'>
                    <label>Name:</label><br/>
                    <input id='e_name'/><br/>
                    <label>Role:</label><br/>
                    <input id='e_role'/><br/>
                    <button onClick={() => this.addEngineer()}>Add Engineer</button>
                </div>
            </div>


            {this.state.tasks.map((value, index) => {
                return (
                    <Task key={index} task={value}/>
                )
            })}
            
            </div>
        );
    }
}

export default App;
