import React, { Component } from 'react';
import Game from './components/Game'
import './App.css';
import * as data from './store/tasks.json'
import * as action from './store/actions.js'
import { act } from 'react-dom/test-utils';
import Task from './components/Task'

export function update() {
    App.setState({tasks: action.getTasks()})
}
class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            tasks: action.getTasks()
        };
      }

    componentWillMount() {
        
    }
    handleAdd() {
        action.addTask(document.getElementById('newtask').value, document.getElementById('newtask_e').value)
        this.update();
    }

    
  
    render() {
        return (
            <div className='body'>

            <div>
                <label>Task:</label>
                <br/>
                
                <textarea id='newtask'/>
                <br/>
                <label>Assigned:</label>
                <select id='newtask_e'>
                {action.getEngineers().map((value,index) => {
                    return (<option value={value.id}>{value.name}</option>)
                })}
                </select>
                <br/>
                <button onClick={() => this.handleAdd()}>Add</button>
            </div>


            {this.state.tasks.map((value, index) => {
                return (
                    <Task key={index} task={value}/>
                )
            })}
            
            </div>
        );
    }
}

export default App;
