import React, { Component } from 'react';
import * as action from '../store/actions.js'

class Task extends Component {

    render() {
        var task = this.props.task
        return (
            <div className='task-container'>
                <label>Task:</label>
                <p>{task.text}</p>
                <label>Assigned:</label>
                <b>{action.getEngineersName(task.personID)}</b>
                <button className='remove' onClick={action.deleteTask(task.id)}>&#10006;</button>
            </div>
        );
    }
}

export default Task;
