import * as data from "./tasks.json"
import fs from "fs"

export function getTasks() {
    return data.tasks
}
export function getPeople() {

}

export function save(callback) {
    var json = JSON.stringify(data);
    fs.writeFile('data.json', json, 'utf8', callback);
}
