import React, { Component } from 'react';
import * as action from '../store/actions.js'

class Task extends Component {

    handleDone(){
        action.taskDone(this.props.task);
        document.getElementById(this.props.task.id).checked=true
    }
    render() {
        var task = this.props.task
        return (
            <div className='task-container'>
                <button className='remove' onClick={() => action.deleteTask(task.id)}>&#10006;</button>
                <label>Task:</label>
                <p>{task.text}</p>
                <label>Assigned:</label>
                <b>{action.getEngineersName(task.personID)}</b> <input type='checkbox' id={this.props.task.id} checked={task.done} onChange={() => this.handleDone()}></input>
            </div>
        );
    }
}

export default Task;
